import React, { useEffect } from "react";

const AboutSection = () => {
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("show");
        } else {
          entry.target.classList.remove("show");
        }
      });
    });

    const hiddenElements = document.querySelectorAll(
      ".fade_up, .fade_down, .zoom_in, .zoom_out, .fade_right, .fade_left, .flip_left, .flip_right, .flip_up, .flip_down"
    );
    hiddenElements.forEach((el) => observer.observe(el));

    return () => {
      hiddenElements.forEach((el) => observer.unobserve(el));
    };
  }, []);
  return (
    <>
      {/* <!-- ====================================== Section About ===================================== --> */}
      <section className="About-section jos" id="about">
        <div className="heading-container">
          <h2 className="section-heading-text about-me fade_up">About Me.</h2>
          <div className="line"></div>
        </div>
        <p className="section-sub-text about-sub-text zoom_in">
          With over 4 years of experience as a game developer, I have a diverse background that spans mobile games, virtual reality projects, consoles, and PC. The majority of my career has been dedicated to Unity, where I successfully published two games on the Android and iOS stores.
         
          In the realm of virtual reality, I contributed to the development of a clinical case simulator aimed at medical students, helping them perform more empathetic consultations and correctly conduct medical examinations. In this project, I developed specific medical exams such as Oropharyngeal Assessment, Abdominal Palpation Exams, Motor Exams, and Reflex Exams. It was also in this context that I discovered my passion for UI development, working closely with the design team to create an accessible and intuitive interface.
          
          My experience also includes porting games to consoles and PCs, where I worked as a freelancer using Unity, Godot, and Unreal. While my experience with Godot and Unreal is more recent, I am deeply committed to continuous learning and growth, always seeking new knowledge and remaining open to new opportunities in the evolving landscape of game development.
        </p>
        <div className="about-detail-main">
          <p className="about-detail">Name</p>
          <p className="about-detail-info">Guilherme Montesano</p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Nationality</p>
          <p className="about-detail-info">Brazilian</p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Phone</p>
          <p
            className="about-detail-info email"
            onClick="location.href='tel:(+1)2345678899'"
          >
            (+55) 11 96346 3959
          </p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Email</p>
          <p
            className="about-detail-info email"
            onClick="location.href='mailto:guilherme.montesano@gmail.com'"
          >
            guilherme.montesano@gmail.com
          </p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Experience</p>
          <p className="about-detail-info">4+ years</p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Freelance</p>
          <p className="about-detail-info">Available</p>
        </div>
        <div className="about-detail-main">
          <p className="about-detail">Language</p>
          <p className="about-detail-info">Brazilian Portuguese | English</p>
        </div>
      </section>
      {/* <!-- ====================================== Section About End ===================================== --> */}
    </>
  );
};
export default AboutSection;
