import React, { useEffect, useState } from "react";
import emailjs from "@emailjs/browser";

const Contact = () => {
    const [inputState, setInputState] = useState({
        name: "",
        email: "",
        subject: "",
        message: "",
    });

    const [isSubmitting, setIsSubmitting] = useState(false);
    const [submitStatus, setSubmitStatus] = useState(null);

    useEffect(() => {
        const inputs = document.querySelectorAll("input");
        inputs.forEach((input) => {
            input.addEventListener("focus", () => {
                if (input.parentElement) input.parentElement.classList.add("active");
            });
            input.addEventListener("focusout", () => {
                if (input.parentElement) {
                    if (input.value === "") {
                        input.parentElement.classList.remove("active");
                    } else {
                        input.parentElement.classList.add("active");
                    }
                }
            });
        });
        return () => { };
    }, []);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setInputState((prevState) => ({
            ...prevState,
            [id]: value,
        }));
    };

    // --- FUNÇÃO QUE ENVIA O E-MAIL ---
    const handleSubmit = (e) => {
        e.preventDefault();

        // Validação simples
        if (!inputState.name || !inputState.email || !inputState.message) {
            alert("Please fill in all required fields.");
            return;
        }

        setIsSubmitting(true);

        const templateParams = {
            name: inputState.name,
            email: inputState.email,
            subject: inputState.subject,
            message: inputState.message,
        };

        // Substitua abaixo pelos seus IDs do EmailJS
        const serviceID = "service_9p15hj8";   // Ex: service_x93...
        const templateID = "template_9vs7bxo"; // Ex: template_8s2...
        const publicKey = "CLWIZi3jcSTb-5fy5";   // Ex: user_9as...

        emailjs
            .send(serviceID, templateID, templateParams, publicKey)
            .then(
                (response) => {
                    console.log("SUCCESS!", response.status, response.text);
                    setSubmitStatus("success");
                    setIsSubmitting(false);
                    setInputState({ name: "", email: "", subject: "", message: "" });
                    alert("Message sent successfully!");
                },
                (err) => {
                    console.log("FAILED...", err);
                    setSubmitStatus("error");
                    setIsSubmitting(false);
                    alert("Failed to send message. Please try again.");
                }
            );
    };

    return (
        <>
            <section className="contact-section" id="contact">
                <div className="heading-container">
                    <h2 className="section-heading-text coding-skill-text fade_up">
                        Get In Touch.
                    </h2>
                    <div className="line"></div>
                </div>

                <div className="main-box-contact">
                    <div className="contact-box-main zoom_in">
                        <div className="mobile-icon-main">
                            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 48 48" fill="none">
                                <path d="M6 6H42C43.1046 6 44 6.89544 44 8V40C44 41.1046 43.1046 42 42 42H6C4.89544 42 4 41.1046 4 40V8C4 6.89544 4.89544 6 6 6ZM24.1212 23.3658L11.2944 12.4754L8.70556 15.5246L24.1462 28.6342L39.3088 15.5123L36.6912 12.4877L24.1212 23.3658Z" fill="white" />
                            </svg>
                        </div>
                        <div>
                            <p className="contact-email-text">E-MAIL</p>
                            <p className="contact-email"><a href="mailto:bmmontesano@gmail.com">bmmontesano@gmail.com</a></p>
                        </div>
                    </div>
                    <div className="contact-box-main zoom_in">
                        <div className="mobile-icon-main">
                            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 48 48" fill="none">
                                <path d="M14 4H36C37.1046 4 38 4.89544 38 6V42C38 43.1046 37.1046 44 36 44H12C10.8954 44 10 43.1046 10 42V0H14V4ZM14 8V18H34V8H14Z" fill="white" />
                            </svg>
                        </div>
                        <div>
                            <p className="contact-email-text">PHONE</p>
                            <p className="contact-email"><a href="tel:+5511963463959">+55 11 96346 3959</a></p>
                        </div>
                    </div>
                </div>

                {/* FORMULÁRIO */}
                <div className="form-container">
                    <div className="form">
                        <label htmlFor="name" className="form__label">NAME</label>
                        <input
                            type="text"
                            className="form__input"
                            placeholder="Your name"
                            id="name"
                            required
                            autoComplete="off"
                            value={inputState.name}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form">
                        <label htmlFor="email" className="form__label">EMAIL</label>
                        <input
                            type="email"
                            className="form__input"
                            placeholder="Your email"
                            id="email"
                            required
                            autoComplete="off"
                            value={inputState.email}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form subject-input-main">
                        <label htmlFor="subject" className="form__label">SUBJECT</label>
                        <input
                            type="text"
                            className="form__input"
                            placeholder="Your subject"
                            id="subject"
                            required
                            autoComplete="off"
                            value={inputState.subject}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form">
                        <label htmlFor="message" className="form__label">MESSAGE</label>
                        {/* Sugestão: Mude input type="text" para textarea para mensagens longas */}
                        <input
                            type="text"
                            className="form__input"
                            placeholder="Write your text..."
                            id="message"
                            required
                            autoComplete="off"
                            value={inputState.message}
                            onChange={handleChange}
                        />
                    </div>
                </div>

                {/* BOTÃO COM EVENTO DE CLIQUE */}
                <div className="wrapper blog-btn">
                    <a
                        className="btn-hover"
                        href="#"
                        onClick={handleSubmit}
                        style={{ cursor: isSubmitting ? "wait" : "pointer" }}
                    >
                        {isSubmitting ? "Sending..." : "Submit Now"}
                    </a>
                </div>

                <div className="footer">© 2025 Bianca</div>
            </section>
        </>
    );
};
export default Contact;
